local a={}function a.setGpu(b)os.log("Changing IO gpu")if b.getScreen()then a.gpu=b;a.w,a.h=a.gpu.getResolution()a.cur_w=1;a.cur_h=1;return true end;return false end;function a.clear()a.gpu.fill(1,1,a.w,a.h," ")a.cur_w=1;a.cur_h=1 end;function a.erase(c)if a.cur_w==1 then return end;a.gpu.fill(a.cur_w-c,a.cur_h,a.cur_w,a.cur_h," ")a.cur_w=a.cur_w-c end;function a.bump()a.gpu.copy(1,2,a.w,a.h,0,-1)a.gpu.fill(1,a.h,a.w,a.h," ")a.cur_h=a.h end;function a.print(d)if a.cur_h>a.h then a.bump()end;a.gpu.set(a.cur_w,a.cur_h,d)a.cur_w=a.cur_w+string.len(d)end;function a.println(d)if type(d)~="string"then d=tostring(d)end;if a.cur_h>a.h then a.bump()end;a.gpu.set(a.cur_w,a.cur_h,d)a.cur_h=a.cur_h+1;a.cur_w=1 end;return a