function os.sleep(a)checkArg(1,a,"number","nil")if kernel.scheduler.current_thread~=nil then thread.sleep(a)else local b=computer.uptime()+(a or 0)repeat computer.pullSignal(b-computer.uptime())until computer.uptime()>=b end end;os.log_file=nil;function os.log(c)if kernel.runlevel()<2 then return end;if type(c)~="string"then c=tostring(c)end;if not os.log_file then os.log_file=fs.open("/log.txt","a")if not os.log_file then kernel.panic("Cannot open file for logging!")end;os.log_file:write("Opened file\n")end;os.log_file:write(c.."\n")end